const Jobs = () => {
  const JobData = JSON.parse(localStorage.getItem("item")) || [];
  const [filteredJobs, setFilteredJobs] = useState([...JobData, ...Job]);
  const [searchterm, setSearchTerm] = useState("");
  const [active, setActive] = useState(false);
  const [selectedJob, setSelectedJob] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);

  // Modal open/close handlers
  function openModal(job) {
    setSelectedJob(job);
    setIsModalOpen(true);
  }

  function closeModal(event) {
    if (event.target.className === "job-modal") {
      setIsModalOpen(false);
      setSelectedJob(null);
    }
  }

  return (
    <>
      <Navbar />
      <div className="jobs-for-you">
        {/* Job list rendering... */}
        {isModalOpen && selectedJob && (
          <div className="job-modal" onClick={closeModal}>
            <div className="modal-content">
              <span className="close-button" onClick={() => setIsModalOpen(false)}>
                &times;
              </span>
              {selectedJob.logo && (
                <img
                  src={
                    selectedJob.logo.length > 20
                      ? selectedJob.logo
                      : require(`../../Assets/images/${selectedJob.logo}`)
                  }
                  alt="Company Logo"
                  className="modal-logo"
                />
              )}
              <h3>{selectedJob.position}</h3>
              <div className="modal-details">
                <div className="modal-row">
                  <strong>Company:</strong> <span>{selectedJob.company}</span>
                </div>
                <div className="modal-row">
                  <strong>Location:</strong> <span>{selectedJob.location}</span>
                </div>
                <div className="modal-row">
                  <strong>Role:</strong> <span>{selectedJob.role}</span>
                </div>
                <div className="modal-row">
                  <strong>Posted:</strong> <span>{selectedJob.posted}</span>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </>
  );
};
