import React, { useState } from 'react';
import "./index.css";


function Login() {
  const [justifyActive, setJustifyActive] = useState('tab1');
  const [loginEmail, setLoginEmail] = useState('');
  const [loginPassword, setLoginPassword] = useState('');

  const handleJustifyClick = (value) => {
    if (value === justifyActive) {
      return;
    }
    setJustifyActive(value);
  };

  const handleLoginSubmit = async (e) => {
    e.preventDefault(); // Prevent the default form submission

    const loginData = {
      email: loginEmail,
      password: loginPassword,
    };

    try {
      const response = await fetch('https://your-backend-url.com/api/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(loginData),
      });

      if (!response.ok) {
        const errorData = await response.json();
        // Handle the error from the backend (e.g., invalid credentials)
        alert(errorData.message || 'Login failed. Please try again.');
        return;
      }

      const data = await response.json();

      // Assuming the backend returns a success status and user info
      if (data.success) {
        // Redirect to the next page or show success message
        alert('Login successful!');
        // For example, you can navigate to the next page using react-router:
        // window.location.href = '/next-page'; // Or use history.push('/next-page') if using react-router
      } else {
        alert('Invalid credentials. Please try again.');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('An error occurred. Please try again later.');
    }
  };

  return (
    <div className="container">
      <div className="tabs">
        <a href="#login" className="tab-link active" onClick={() => handleJustifyClick('tab1')}>Login</a>
        <a href="#register" className="tab-link" onClick={() => handleJustifyClick('tab2')}>Register</a>
      </div>

      <div id="login" className="tab-content" style={{ display: justifyActive === 'tab1' ? 'block' : 'none' }}>
        <div className="center-text">
          <p>Sign in with:</p>
          <div className="social-buttons">
            <a href="#" className="social-button">Facebook</a>
            <a href="#" className="social-button">Twitter</a>
            <a href="#" className="social-button">Google</a>
            <a href="#" className="social-button">GitHub</a>
          </div>
          <p>or:</p>
        </div>

        <form onSubmit={handleLoginSubmit}>
          <div className="form-group">
            <label htmlFor="login-email">Email address</label>
            <input
              type="email"
              id="login-email"
              value={loginEmail}
              onChange={(e) => setLoginEmail(e.target.value)}
              required
            />
          </div>
          <div className="form-group">
            <label htmlFor="login-password">Password</label>
            <input
              type="password"
              id="login-password"
              value={loginPassword}
              onChange={(e) => setLoginPassword(e.target.value)}
              required
            />
          </div>

          <div className="checkbox-group">
            <label><input type="checkbox" /> Remember me</label>
            <a href="#">Forgot password?</a>
          </div>

          <button type="submit" className="button">Sign in</button>
          <p className="center-text">Not a member? <a href="#!" onClick={() => handleJustifyClick('tab2')}>Register</a></p>
        </form>
      </div>

      <div id="register" className="tab-content" style={{ display: justifyActive === 'tab2' ? 'block' : 'none' }}>
        <div className="center-text">
          <p>Sign up with:</p>
          <div className="social-buttons">
            <a href="#" className="social-button">Facebook</a>
            <a href="#" className="social-button">Twitter</a>
            <a href="#" className="social-button">Google</a>
            <a href="#" className="social-button">GitHub</a>
          </div>
          <p>or:</p>
        </div>

        <div className="form-group">
          <label htmlFor="register-name">Name</label>
          <input type="text" id="register-name" required />
        </div>
        <div className="form-group">
          <label htmlFor="register-username">Username</label>
          <input type="text" id="register-username" required />
        </div>
        <div className="form-group">
          <label htmlFor="register-email">Email</label>
          <input type="email" id="register-email" required />
        </div>
        <div className="form-group">
          <label htmlFor="register-password">Password</label>
          <input type="password" id="register-password" required />
        </div>

        <div className="checkbox-group">
          <label><input type="checkbox" /> I have read and agree to the terms</label>
        </div>

        <button className="buttonn">Sign up</button>
      </div>
    </div>
  );
}

export default Login;
